/***********************************************
 * TEMPLATE: STICKY NAVBAR
 ***********************************************/
(function($) {

    'use strict';

    SMJS.stickyNavbar = {

        init: function() {

            var navbarMain = $('.sm-header:not(.sm-header--slide) .sm-navbar--main');

            navbarMain.each(function() {

                var $this = $(this);

                // sticky navbar
                var navbarHeight = $this.length ? $this.outerHeight() : 0,
                    navbarMainOffset = $this.hasClass('sm-navbar--offset') ? SMJS.window.outerHeight() : navbarHeight;

                // fake navbar
                var navbarFake = $('<div class="sm-fake-navbar">').hide();

                function _fixed_navbar() {

                    $this.addClass('sm-navbar--fixed');
                    navbarFake.show();

                }

                function _unfixed_navbar() {

                    $this.removeClass('sm-navbar--fixed');
                    navbarFake.hide();

                }

                function _on_scroll_navbar() {

                    if (SMJS.window.scrollTop() >= navbarMainOffset) {

                        _fixed_navbar();

                    } else {

                        _unfixed_navbar();

                    }

                }

                if ($this.hasClass('sm-navbar--sticky')) {

                    SMJS.window.on('scroll resize', _on_scroll_navbar);

                    _on_scroll_navbar();

                    // append fake navbar
                    $this.after(navbarFake);

                    // fake navbar height after resize
                    navbarFake.height($this.innerHeight());

                    SMJS.debounceResize(function() {
                        navbarFake.height($this.innerHeight());
                    });

                }

                // hide navbar on scroll
                var navbarHideOnScroll = $this.filter('.sm-navbar--hide-on-scroll');

                SMJS.throttleScroll(function(type, scroll) {

                    var start = 450;

                    function _show_navbar() {
                        navbarHideOnScroll.removeClass('sm-on-scroll-hide').addClass('sm-on-scroll-show');
                    }

                    function _hide_navbar() {
                        navbarHideOnScroll.removeClass('sm-on-scroll-show').addClass('sm-on-scroll-hide');
                    }

                    // hide or show
                    if (type === 'down' && scroll > start) {
                        _hide_navbar();
                    } else if (type === 'up' || type === 'end' || type === 'start') {
                        _show_navbar();
                    }

                    // add solid color
                    if ($this.hasClass('sm-navbar--transparent') && $this.hasClass('sm-navbar--sticky')) {
                        scroll > navbarHeight ? $this.addClass('sm-navbar--solid') : $this.removeClass('sm-navbar--solid');
                    }

                });

            });

        }

    };

    SMJS.stickyNavbar.init();

})(jQuery);
