/***********************************************
 * WIDGET: SHOWCASE 1
 ***********************************************/
(function($) {

    'use strict';

    // check if plugin defined
    if (typeof $.fn.pagepiling == 'undefined') {
        return;
    }

    SMJS.showcase1 = {

        init: function() {

            var fullpageSlider = $('.sm-showcase-1');

            if (!fullpageSlider.length) {
                return;
            }

            SMJS.body.css('overflow', 'hidden');
            SMJS.html.css('overflow', 'hidden');

            fullpageSlider.pagepiling({
                scrollingSpeed: 800,
                loopBottom: true,
                navigation: false,
                afterLoad: function(anchorLink, index) {
                    var current = fullpageSlider.find('.section.active');

                    if (index > 1) {
                        $('.sm-showcase-fixed-footer--inner, .sm-showcase-fixed-footer--outer').addClass('active');
                    } else {
                        $('.sm-showcase-fixed-footer--inner, .sm-showcase-fixed-footer--outer').removeClass('active');
                    }

                    if (current.hasClass('dark')) {
                        setDark();
                    } else {
                        removeDark();
                    }
                }
            });

            function setDark() {
                $('.sm-header .sm-navbar').addClass('sm-navbar--white-text-on-top');
                $('.sm-showcase-fixed-footer--inner, .sm-showcase-fixed-footer--outer').addClass('dark');
            }

            function removeDark() {
                $('.sm-header .sm-navbar').removeClass('sm-navbar--white-text-on-top');
                $('.sm-showcase-fixed-footer--inner, .sm-showcase-fixed-footer--outer').removeClass('dark');
            }

        }
    };

    SMJS.showcase1.init();

})(jQuery);
